            <button onClick={() => this.socket.emit("start")}>start</button>
            <button onClick={() => this.socket.emit("check")}>Check</button>

            {/*
                <div>"Your time remaining is: " {Component.turnSeconds}</div>
                */}
        </>
    );
}


}
